import { NextResponse } from 'next/server';
import { runNotificationEngine, type Matter } from '@/lib/notificationEngine';

// This endpoint will be called by Vercel Cron or a scheduler
// For now, it uses mock data. In production, it will fetch from the database.

export async function GET(request: Request) {
    try {
        // TODO: Replace with actual database query
        // const matters = await prisma.matter.findMany({ where: { status: 'active' } });

        // Mock data for demonstration
        const mockMatters: Matter[] = [
            {
                id: '1',
                caseNumber: 'ID/1234/2025',
                name: 'State v. Johnson',
                clientId: 'client-1',
                assignedLawyerId: 'lawyer-1',
                court: 'High Court Lagos',
                status: 'active',
                nextCourtDate: new Date(Date.now() + 5 * 24 * 60 * 60 * 1000), // 5 days from now
                lastActivityAt: new Date(Date.now() - 20 * 24 * 60 * 60 * 1000), // 20 days ago
                lastClientContact: new Date(Date.now() - 25 * 24 * 60 * 60 * 1000), // 25 days ago
                createdAt: new Date(Date.now() - 60 * 24 * 60 * 60 * 1000),
            },
            {
                id: '2',
                caseNumber: 'ID/5678/2025',
                name: 'TechCorp v. FirstBank',
                clientId: 'client-2',
                assignedLawyerId: 'lawyer-2',
                status: 'active',
                lastActivityAt: new Date(Date.now() - 35 * 24 * 60 * 60 * 1000), // 35 days ago - CRITICAL
                createdAt: new Date(Date.now() - 90 * 24 * 60 * 60 * 1000),
            },
        ];

        // Run the notification engine
        const notifications = runNotificationEngine(mockMatters);

        // TODO: Save notifications to database
        // await prisma.notification.createMany({ data: notifications });

        // TODO: Send emails/SMS for high-priority notifications
        // for (const notif of notifications.filter(n => n.priority === 'critical' || n.priority === 'high')) {
        //   await sendEmail(notif);
        // }

        return NextResponse.json({
            success: true,
            notificationsGenerated: notifications.length,
            notifications: notifications.map(n => ({
                id: n.id,
                title: n.title,
                priority: n.priority,
                recipientId: n.recipientId,
            })),
        });
    } catch (error) {
        console.error('Notification engine error:', error);
        return NextResponse.json(
            { success: false, error: 'Failed to run notification engine' },
            { status: 500 }
        );
    }
}

"use client";

import { useState } from 'react';
import { FileText, Image, FileCheck, Upload, Download, Eye, Loader } from 'lucide-react';
import styles from './DocumentList.module.css';

interface Document {
    id: string;
    name: string;
    type: 'pdf' | 'docx' | 'image' | 'scan';
    size: number;
    uploadedAt: Date;
    ocrStatus?: 'pending' | 'processing' | 'completed' | 'failed';
    ocrText?: string;
}

interface DocumentListProps {
    briefId: string;
    onDocumentClick: (document: Document) => void;
}

const MOCK_DOCUMENTS: Document[] = [
    {
        id: '1',
        name: 'Motion for Bail.pdf',
        type: 'pdf',
        size: 2400000,
        uploadedAt: new Date('2025-10-15'),
        ocrStatus: 'completed',
    },
    {
        id: '2',
        name: 'Affidavit of Facts.pdf',
        type: 'scan',
        size: 1800000,
        uploadedAt: new Date('2025-10-14'),
        ocrStatus: 'processing',
    },
    {
        id: '3',
        name: 'Evidence Photo 1.jpg',
        type: 'image',
        size: 450000,
        uploadedAt: new Date('2025-10-13'),
        ocrStatus: 'completed',
    },
    {
        id: '4',
        name: 'Court Order.pdf',
        type: 'pdf',
        size: 980000,
        uploadedAt: new Date('2025-10-12'),
    },
    {
        id: '5',
        name: 'Written Address.docx',
        type: 'docx',
        size: 320000,
        uploadedAt: new Date('2025-10-11'),
    },
];

const DocumentList = ({ briefId, onDocumentClick }: DocumentListProps) => {
    const [documents] = useState<Document[]>(MOCK_DOCUMENTS);
    const [uploadProgress, setUploadProgress] = useState(0);
    const [isUploading, setIsUploading] = useState(false);

    const formatFileSize = (bytes: number) => {
        if (bytes < 1024) return bytes + ' B';
        if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
        return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
    };

    const getFileIcon = (type: string) => {
        switch (type) {
            case 'image':
                return <Image size={20} />;
            case 'scan':
                return <FileCheck size={20} />;
            default:
                return <FileText size={20} />;
        }
    };

    const getOCRStatusBadge = (status?: string) => {
        if (!status) return null;

        const badges = {
            pending: { text: 'OCR Pending', className: styles.ocrPending },
            processing: { text: 'Processing OCR...', className: styles.ocrProcessing },
            completed: { text: 'OCR Complete', className: styles.ocrCompleted },
            failed: { text: 'OCR Failed', className: styles.ocrFailed },
        };

        const badge = badges[status as keyof typeof badges];
        if (!badge) return null;

        return (
            <span className={badge.className}>
                {status === 'processing' && <Loader size={12} className={styles.spinner} />}
                {badge.text}
            </span>
        );
    };

    const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const files = e.target.files;
        if (!files || files.length === 0) return;

        setIsUploading(true);
        // TODO: Implement actual upload with OCR processing
        // For now, just simulate upload
        for (let i = 0; i <= 100; i += 10) {
            await new Promise(resolve => setTimeout(resolve, 100));
            setUploadProgress(i);
        }
        setIsUploading(false);
        setUploadProgress(0);
    };

    return (
        <div className={styles.container}>
            <div className={styles.header}>
                <div>
                    <h2 className={styles.title}>Documents ({documents.length})</h2>
                    <p className={styles.subtitle}>All documents in this brief</p>
                </div>
                <label className={styles.uploadBtn}>
                    <Upload size={18} />
                    <span>Upload Document</span>
                    <input
                        type="file"
                        multiple
                        accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                        onChange={handleFileUpload}
                        className={styles.fileInput}
                    />
                </label>
            </div>

            {isUploading && (
                <div className={styles.uploadProgress}>
                    <div className={styles.progressBar}>
                        <div className={styles.progressFill} style={{ width: `${uploadProgress}%` }} />
                    </div>
                    <span className={styles.progressText}>Uploading... {uploadProgress}%</span>
                </div>
            )}

            <div className={styles.documentList}>
                {documents.map((doc) => (
                    <div
                        key={doc.id}
                        className={styles.documentItem}
                        onClick={() => onDocumentClick(doc)}
                    >
                        <div className={styles.documentIcon}>
                            {getFileIcon(doc.type)}
                        </div>
                        <div className={styles.documentInfo}>
                            <div className={styles.documentName}>{doc.name}</div>
                            <div className={styles.documentMeta}>
                                <span>{formatFileSize(doc.size)}</span>
                                <span>ΓÇó</span>
                                <span>{doc.uploadedAt.toLocaleDateString()}</span>
                                {getOCRStatusBadge(doc.ocrStatus)}
                            </div>
                        </div>
                        <div className={styles.documentActions}>
                            <button className={styles.actionBtn} title="Preview">
                                <Eye size={16} />
                            </button>
                            <button className={styles.actionBtn} title="Download">
                                <Download size={16} />
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DocumentList;

import { FileText, Download, Share2, Printer, Maximize2, ZoomIn, ZoomOut, Image as ImageIcon, FileSpreadsheet, File as FileIcon } from 'lucide-react';
import styles from './DocumentViewer.module.css';

interface Document {
    id: string;
    name: string;
    type: 'pdf' | 'docx' | 'image' | 'scan';
    size: number;
    uploadedAt: Date;
    ocrStatus?: 'pending' | 'processing' | 'completed' | 'failed';
    ocrText?: string;
}

interface DocumentViewerProps {
    document: Document;
}

const DocumentViewer = ({ document }: DocumentViewerProps) => {
    const renderContent = () => {
        switch (document.type) {
            case 'pdf':
            case 'scan':
                return (
                    <div className={styles.placeholder}>
                        <div className={styles.previewPage}>
                            <div className={styles.previewHeader}>
                                <h3>PREVIEW: {document.name}</h3>
                                <p>PDF / Scanned Document Viewer</p>
                            </div>
                            <div className={styles.previewContent}>
                                <p>This is a placeholder for the PDF viewer integration (e.g., react-pdf).</p>
                                <br />
                                <p><strong>Document ID:</strong> {document.id}</p>
                                <p><strong>Uploaded:</strong> {document.uploadedAt.toLocaleDateString()}</p>
                                <br />
                                {document.ocrText && (
                                    <div className={styles.ocrSection}>
                                        <h4>OCR Extracted Text:</h4>
                                        <p>{document.ocrText.substring(0, 500)}...</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                );
            case 'image':
                return (
                    <div className={styles.imageContainer}>
                        <div className={styles.imagePlaceholder}>
                            <ImageIcon size={64} className={styles.placeholderIcon} />
                            <p>Image Preview: {document.name}</p>
                        </div>
                    </div>
                );
            case 'docx':
                return (
                    <div className={styles.placeholder}>
                        <div className={styles.previewPage}>
                            <div className={styles.previewHeader}>
                                <FileText size={48} />
                                <h3>Word Document</h3>
                            </div>
                            <div className={styles.previewContent}>
                                <p>Preview not available for DOCX files yet.</p>
                                <button className={styles.downloadBtn}>
                                    <Download size={16} /> Download to View
                                </button>
                            </div>
                        </div>
                    </div>
                );
            default:
                return (
                    <div className={styles.placeholder}>
                        <div className={styles.previewPage}>
                            <div className={styles.previewHeader}>
                                <FileIcon size={48} />
                                <h3>Unsupported File Type</h3>
                            </div>
                            <div className={styles.previewContent}>
                                <p>This file type cannot be previewed directly.</p>
                            </div>
                        </div>
                    </div>
                );
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.toolbar}>
                <div className={styles.docInfo}>
                    <FileText size={20} className={styles.docIcon} />
                    <span className={styles.docName}>{document.name}</span>
                </div>
                <div className={styles.actions}>
                    <button className={styles.actionBtn} title="Zoom Out">
                        <ZoomOut size={18} />
                    </button>
                    <button className={styles.actionBtn} title="Zoom In">
                        <ZoomIn size={18} />
                    </button>
                    <div className={styles.divider} />
                    <button className={styles.actionBtn} title="Print">
                        <Printer size={18} />
                    </button>
                    <button className={styles.actionBtn} title="Download">
                        <Download size={18} />
                    </button>
                    <button className={styles.actionBtn} title="Share">
                        <Share2 size={18} />
                    </button>
                    <div className={styles.divider} />
                    <button className={styles.actionBtn} title="Fullscreen">
                        <Maximize2 size={18} />
                    </button>
                </div>
            </div>

            <div className={styles.viewer}>
                {renderContent()}
            </div>
        </div>
    );
};

export default DocumentViewer;

